if not getBuildingMenuInstance then
    require("BuildingMenu01_Main")
end

local BuildingMenu = getBuildingMenuInstance()

function BuildingMenu.addObjectsToCategories(categoryName, categoryIcon, subcategoryName, subCategoryIcon, objects)
    if BuildingMenu.Categories then
        -- find the category, or create it if it doesn't exist
        local category
        for _, cat in ipairs(BuildingMenu.Categories) do
            if cat.categoryName == categoryName then
                category = cat
                break
            end
        end
        if not category then
            category = {
                categoryName = categoryName,
                subcategories = {}
            }
            if categoryIcon then
                category.categoryIcon = categoryIcon
            end
            table.insert(BuildingMenu.Categories, category)
        elseif categoryIcon then 
            category.categoryIcon = categoryIcon
        end

        local subcategory
        for _, subcat in ipairs(category.subcategories) do
            if subcat.subcategoryName == subcategoryName then
                subcategory = subcat
                break
            end
        end
        if not subcategory then
            subcategory = {
                subcategoryName = subcategoryName,
                objects = {}
            }
            if subCategoryIcon then
                subcategory.subCategoryIcon = subCategoryIcon
            end
            table.insert(category.subcategories, subcategory)
        elseif subCategoryIcon then 
            subcategory.subCategoryIcon = subCategoryIcon
        end

        -- add the objects to the subcategory
        for _, obj in ipairs(objects) do
            table.insert(subcategory.objects, obj)
        end
    end
end



function BuildingMenu.createObject(name, description, action, recipe, isRecipeKnown, options, sprites)
    return {
        name = getTextOrNull(name) or BuildingMenu.getMoveableDisplayName(sprites.sprite) or sprites.sprite,
        description = getTextOrNull(description) or "",
        data = {
            action = action,
            recipe = recipe,
            isRecipeKnown = isRecipeKnown,
            options = options,
            sprites = sprites
        }
    }
end

